interface Store {
  currentPage: number;
  feeds: NewsFeed[];
}

interface News {
  readonly id: number;
  readonly time_ago: string;
  readonly title: string;
  readonly url: string;
  readonly user: string;
  readonly content: string;
}

interface NewsFeed extends News {
  readonly comments_count: number;
  readonly points: number;
  read?: boolean;
}

interface NewsDetail extends News {
  readonly comments: NewsComment[];
}

interface NewsComment extends News {
  readonly comments: NewsComment[];
  readonly level: number;
}

const container: HTMLElement | null = document.getElementById("root");
const ajax: XMLHttpRequest = new XMLHttpRequest();
const content = document.createElement("div");
const NEWS_URL = "https://api.hnpwa.com/v0/news/1.json";
const CONTENT_URL = "https://api.hnpwa.com/v0/item/@id.json";
const store: Store = {
  currentPage: 1,
  feeds: [],
};

class Api {
  getRequest<AjaxResponse>(url: string): AjaxResponse {
    //Get은 method / 그다음 주소는 가져올 url / 마지막은 비동기를 사용할지 boolean으로 변환
    const ajax = new XMLHttpRequest();
    ajax.open("GET", url, false);
    ajax.send();
    return JSON.parse(ajax.response); //newsFeed=> 상수는 ajax.response값을 json형태로(보기쉽게) 저장
  }
}

function applyApiMixins(targetClass: any, baseClass: any[]): void {
  //처음들어오는 targetClass 인자는 나중에들어오는 baseClass 인자를 상속한다.
  baseClass.forEach((baseClass) => {
    Object.getOwnPropertyNames(baseClass.prototype).forEach((name) => {
      const descriptor = Object.getOwnPropertyDescriptor(
        baseClass.prototype,
        name
      );
      if (descriptor) {
        Object.defineProperty(targetClass.prototype, name, descriptor);
      }
    });
  });
}

class NewsFeedApi {
  getData(): NewsFeed[] {
    return this.getRequest<NewsFeed[]>(NEWS_URL);
  }
}

class NewsDetailApi {
  getData(id: string): NewsDetail {
    return this.getRequest<NewsDetail>(CONTENT_URL.replace("@id", id));
  }
}

interface NewsFeedApi extends Api {}
interface NewsDetailApi extends Api {}

applyApiMixins(NewsFeedApi, [Api]);
applyApiMixins(NewsDetailApi, [Api]);

class View {
  constructor() {}
}

class NewsFeedView extends View {
  constructor() {
    const api = new NewsFeedApi();
    let newsFeed: NewsFeed[] = store.feeds;

    let template = `
      <div class='bg-gray-600 min-h-screen'>
          <div class="bg-white text-xl">
              <div class='mx-auto p-4'>
                  <div class="flex justify-between items-center py-6">
                      <div class="flex justify-start">
                          <h1 class="font-extrabold">Hackers News</h1>
                      </div>
                      <div class="items-center justify-end">
                          <a href="#/page/{{__prev_page__}}" class="text-gray-500">
                              Previous
                          </a>
                          <a href="#/page/{{__next_page__}}" class="text-gray-500 ml-4">
                              Next
                          </a>
                      </div>
                  </div>
              </div>    
          </div>
          <div class="p-4 text-2xl text-gray-700">
              {{__news_feed__}}
          </div>
      </div>
    `;

    if (newsFeed.length === 0) {
      newsFeed = store.feeds = makeFeeds(api.getData());
    }
  }

  render(): void {
    const newsList = [];
    for (
      let i = (store.currentPage - 1) * 10;
      i < store.currentPage * 10;
      i++
    ) {
      newsList.push(`
        <div class="p-6 ${
          newsFeed[i].read ? "bg-red-500" : "bg-white"
        } mt-6 rounded-lg shadow-md transition-colors duration-500 hover:bg-green-100">
          <div class="flex">
            <div class="flex-auto">
              <a href="#/show/${newsFeed[i].id}">${newsFeed[i].title}</a>  
            </div>
            <div class="text-center text-sm">
              <div class="w-10 text-white bg-green-300 rounded-lg px-0 py-2">${
                newsFeed[i].comments_count
              }</div>
            </div>
          </div>
          <div class="flex mt-3">
            <div class="grid grid-cols-3 text-sm text-gray-500">
              <div><i class="fas fa-user mr-1"></i>${newsFeed[i].user}</div>
              <div><i class="fas fa-heart mr-1"></i>${newsFeed[i].points}</div>
              <div><i class="far fa-clock mr-1"></i>${
                newsFeed[i].time_ago
              }</div>
            </div>  
          </div>
        </div>    
      `);
    }
    template = template.replace("{{__news_feed__}}", newsList.join(""));
    template = template.replace(
      "{{__prev_page__}}",
      String(store.currentPage > 1 ? store.currentPage - 1 : 1)
    );
    template = template.replace(
      "{{__next_page__}}",
      String(store.currentPage < 3 ? store.currentPage + 1 : 3)
    );
    updateView(template);
  }
  makeFeeds(feeds: NewsFeed[]): NewsFeed[] {
    for (let i = 0; i < feeds.length; i++) {
      feeds[i].read = false;
    }
    return feeds;
  }
}

class NewsDetailView extends View {
  constructor() {
    let template = `
        <div class="bg-#424242 min-h-screen pb-8">
          <div class="bg-white text-xl">
            <div class="mx-auto px-4">
              <div class="flex justify-between items-center py-6">
                <div class="flex justify-start">
                  <h1 class="font-extrabold">Hacker News</h1>
                </div>
                <div class="items-center justify-end">
                  <a href="#/page/${store.currentPage}" class="text-gray-500">
                    <i class="fa fa-times"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
    
          <div class="h-full border rounded-xl bg-white m-6 p-4 ">
            <h2>${newsContent.title}</h2>
            <div class="text-gray-400 h-20">
              ${newsContent.content}
            </div>
    
            {{__comments__}}
    
          </div>
        </div>
      `;
  }
  render() {
    const id = location.hash.substring(7);
    const api = new NewsDetailApi();
    const newsContent: NewsDetail = api.getData(id);
    for (let i = 0; i < store.feeds.length; i++) {
      if (store.feeds[i].id === Number(id)) {
        store.feeds[i].read = true;
        break;
      }
    }
    updateView(
      template.replace("{{__comments__}}", makeComment(newsContent.comments))
    );
  }
  makeComment(comments: NewsComment[]): string {
    const commentString = [];
    for (let i = 0; i < comments.length; i++) {
      const comment: NewsComment = comments[i];
      commentString.push(`
          <div style="padding-left: ${comment.level * 40}px;" class="mt-4">
            <div class="text-gray-400">
              <i class="fa fa-sort-up mr-2"></i>
              <strong>${comment.user}</strong> ${comment.time_ago}
            </div>
            <p class="text-gray-700">${comment.content}</p>
          </div>      
        `);

      if (comment.comments.length > 0) {
        commentString.push(makeComment(comment.comments));
      }
    }

    return commentString.join("");
  }
}

function updateView(html: string): void {
  if (container != null) {
    container.innerHTML = html;
  } else {
    console.error("최상위 컨테이너가 없어 UI를 진행하지 못합니다");
  }
}

function newsFeed(): void {}

function newsDetail(): void {}

function router(): void {
  const routePath = location.hash; //location.hash에 #만 있을경우는 비어있다고 봄
  if (routePath === "") {
    newsFeed();
  } else if (routePath.indexOf("#/page/") >= 0) {
    store.currentPage = Number(routePath.substring(7));
    newsFeed();
  } else {
    newsDetail();
  }
}

window.addEventListener("hashchange", router);
router();
